import React from 'react'
import LeftSidebar from '../../components/LeftSidebar/LeftSidebar'
import Avtar from '../../components/Avtar/Avtar'
import { useSelector } from 'react-redux'
import { useParams } from 'react-router-dom'
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import {faBirthdayCake, faPen} from '@fortawesome/free-solid-svg-icons'
import moment from 'moment'

const UserProfile = () => {
    const {id}=useParams()
    const users =useSelector((state)=>state.usersReducer)
    const currentProfile = users.filter((user)=>user._id===id)[0]

   const currentUser = useSelector((state)=>state.currentUsersReducer)
   const handleSwitch =()=>{

   }


  return (
    <div className='home-container-1'>
    <LeftSidebar></LeftSidebar>
    <div className='home-container-2'>
    <section>
        <div className='user-details-container'>
            <div className='user-details'>
            <Avtar  backgroundColor='purple' color='white' fontSize='50px' px='40px' py='30px' >
            {currentProfile?.name.charAt(0).toUpperCase()}

            </Avtar>
            <div className='user-name'>
            <h1>{currentProfile?.name}</h1>
            <p>
                                    <FontAwesomeIcon icon={faBirthdayCake} />{' '}
                                    Member for {moment(currentProfile?.joinedOn).fromNow()}
                                </p>

            </div>



            </div>
            {
                currentUser?.user?._id===id&&(
                    <button type='button' onClick={handleSwitch} className='edit-profile-btn'>
                    <FontAwesomeIcon icon={faPen}/>
                    Edit Profile</button>
                )
            }
        </div>
    </section>

    </div>

    </div>
  )
}

export default UserProfile
